const editProduct = async (cod, description, size) => {
    const body = {
        cod: id,
        description: des,
        size: size
    }

    console.log(`edit; ${des}`)

    const response = await fetch(`http://localhost:3333/editar/${id}`, {
        method: 'POST',
        headers: {'Content-Type': 'application/json',
                    'cookies': localStorage.getItem('jwt')},
        body: JSON.stringify({
            cod: data.codeInput,
            description: data.descriptionInput,
            size: data.sizeInput
        }),
        credentials: 'include'
    })

    const data = await response.json()
    return data
}

export default editProduct
