import { useEffect } from "react"
import getProducts from "../utils/getProducts"

export default function Edit() {

    useEffect(() => {
        const form = document.querySelector('#new-item-form')
        const codField = document.querySelector('#codeInput')
        const descriptionField = document.querySelector('#descriptionInput')
        const sizeField = document.querySelector('#sizeInput')
        const url = new URL(window.location.href)
        const id = url.searchParams.get('id')

        async function loadInputContent(){
            const data = await getProducts()
            data.forEach(element => {
                if (element.cod == id) {
                    codField.value = element.cod
                    descriptionField.value = element.description
                    sizeField.value = element.size
                }
            });
        }

        form.addEventListener('submit', async (e) => {
            e.preventDefault()
        
            const body = {
                cod: document.querySelector('#codeInput').value,
                description: String(document.querySelector('#descriptionInput').value),
                size: String(document.querySelector('#sizeInput').value)
            }
        
            const {cod, description, size} = body
        
            const res = await productService.editProduct(cod, description, size)
        
            if (res.message == 'updated!') {
                window.alert('Registro editado com sucesso!')
            }
        })
        

        loadInputContent()
    }, [])

    return (
        <>
            <form id="new-item-form">
                <h2>Editar Produto</h2>
                <div className="input-field">
                    <label htmlFor="codeInput">Código</label>
                    <input type="number" id="codeInput"/>
                    <label htmlFor="descriptionInput">Descrição</label>
                    <input type="text" id="descriptionInput"/>
                    <label htmlFor="sizeInput">Tamanho</label>
                    <input type="text" id="sizeInput"/>
                </div>
                <div className="button-field">
                    <input type="submit" id="btnSend" value="Editar"/>
                </div>
            </form>
        </>
    )
}
