const editProduct = async (id, des, size) => {
    const body = {
        cod: id,
        description: des,
        size: size
    }

    const response = await fetch(`http://localhost:3333/alterar/${id}`, {
        method: 'PUT',
        headers: {'Content-type': 'application/json'},
        body: JSON.stringify(body)
    })
    return response.json()
}

export default editProduct
