import { useEffect } from "react"

export default function RenderProducts() {

    useEffect(() => {
        const display = document.querySelector('.display')
        async function getProducts() {
            const res = await fetch('http://localhost:3333/produtos', {
                headers: {
                    cookies: localStorage.getItem('jwt'),
                },
                credentials: 'include'
                })
        
            const body = await res.json()
        
            body.map((element) => {
                const newRow = document.createElement('tr')
                const newItem = `<td>${element.cod}</td>
                <td>${element.description}</td><td>${element.description}</td><td></td>`
                newRow.innerHTML += newItem
                display.appendChild(newRow)
            })
        }

        getProducts()
    },[])

    return (
        <>
            <table>
                <thead>
                    <tr>
                        <td>Cod.</td>
                        <td>Descrição</td>
                        <td>Tamanho</td>
                        <td>Ação</td>
                    </tr>
                </thead>
                <tbody className="display">
                    <tr>
                        <td>1</td>
                        <td>parafuso</td>
                        <td>10mm</td>
                        <td><a href="#">Editar</a><a href="#">Excluir</a></td>
                    </tr>
                </tbody>
            </table>
        </>
    )
}
