import { useEffect } from "react"
import render from "../utils/render"
import deleteProduct from "../utils/deleteProduct"

export default function RenderProducts() {

    function newProduct(cod, description, size){
        const newRow = document.createElement('tr')
        const structure = `
        <tr>
        <td data-cod>${cod}</td>
        <td data-description>${description}</td>
        <td data-size>${size}</td>
        <td data-control>
        <ul class="tabela__botoes-controle">
        <li><a href="../pages/editar.html?id=${cod}" class="botao-simples botao-simples--editar">Editar</a></li>
        <li><button class="botao-simples botao-simples--excluir" type="button">Excluir</button></li>
        </ul>
        </td>
        </tr>
        ` 
        newRow.innerHTML = structure
        newRow.dataset.id = cod
        return newRow
    }

    async function getProducts() {
        const res = await fetch('http://localhost:3333/produtos', {
            headers: {
                cookies: localStorage.getItem('jwt'),
            },
            credentials: 'include'
            })
    
        const body = await res.json()
        return body
    }
    
    useEffect(() => {
        const display = document.querySelector('.display')
        
        getProducts().then(products => {
            render(products, display, newProduct)
        })

        display.addEventListener('click', async (e) => {
            e.preventDefault()
        
            const row = e.target.closest('[data-id]')
            const id = row.dataset.id
            
            if (e.target.classList.contains('botao-simples--excluir')) {
                const res = await deleteProduct(id)
                console.log(res)
                
                // if (res.message == 'deleted!') {
                //         row.remove()
                //         window.alert('Produto deletado com sucesso!')
                //     }
        
                // } 
                // else {
                //     if (e.target.classList.contains('botao-simples--editar')) {
                //         window.location.href = `../pages/editar.html?id=${id}`
                //     }
            }
        })
    },[])

    return (
        <>
            <table>
                <thead>
                    <tr>
                        <td>Cod.</td>
                        <td>Descrição</td>
                        <td>Tamanho</td>
                        <td>Ação</td>
                    </tr>
                </thead>
                <tbody className="display">
                </tbody>
            </table>
        </>
    )
}
